$.fn.checkRepetition = function (pLen, str) {
  var res = "";
  for (var i = 0; i < str.length; i++ ) {
    var repeated = true;
    
    for (var j = 0; j < pLen && (j+i+pLen) < str.length; j++) {
      repeated = repeated && (str.charAt(j + i) == str.charAt(j + i + pLen));
    }
    if (j < pLen) {
      repeated = false;
    }
    
    if (repeated) {
      i += pLen - 1;
      repeated = false;
    } else {
      res += str.charAt(i);
    }
  }
  return res;
};
